@js:
bookId = baseUrl.match(/\/book\/(\d+)\//)[1];
headers={
    "content-type": "application/x-www-form-urlencoded",
    "user-agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/130.0.0.0 Safari/537.36",
    "referer": `https://www.xiashu8.com/book/${bookId}/`,
    "x-requested-with": "XMLHttpRequest"
};
downloadInfo = '';
resp = String(java.ajax("https://www.xiashu8.com/e/action/getdownload.php,"+JSON.stringify({
    "method": "POST",
    "headers": headers,
    "body": `dataId=${bookId}&time=${Date.now()}`
})));

if(resp.includes("downloadLinks")){
links = JSON.parse(resp);
links.downloadLinks.forEach(link => {
    downloadInfo += `${link.site}：${link.url}\n`;
});

//downloadLinks = links.downloadLinks.map(link => (link.url));

downloadLinks = links.downloadLinks.map(link => {
    url = link.url;
    isDirectDownload = url.match(/\.(txt|epub|zip|rar|mobi)$/i);
    if (isDirectDownload) {
        java.log("🔙返回直链"+url);
        return url;
    } else if(url.includes('12345676.xyz')){
       try{
        response = String(java.ajax(url));
        directLinkRegex = /url=([^"]+)/;
        match = response.match(directLinkRegex);
        if (match) {
            java.log("📥获取到直链" + match[1]);
            return match[1];
        } else {
            java.log("🔙未获取到直链，返回链接"+url);
        	   return url;
        }
       }catch(error){
            java.log("⚠️请求超时或出错，返回链接" + url);
            return url;
       }
    }else if(url.includes('dl.txtaoye')){
        id = url.match(/\/txt\/(\d+)/)[1];
        directUrl = `https://dl.txtaoye.com/txt/${id[0]}/${id}.txt`;
        java.log("📥获取到直链" + directUrl);
        return directUrl;
     }
     else{
        java.log("🔙返回链接"+url);
        return url;
     }
});

source.setVariable(downloadLinks.join("\n\n"));
java.put("downloadLinks", downloadLinks);
}
else{
downloadInfo="暂无下载地址※=○☆(＿＿*)Ｚｚｚ";
java.toast("暂无下载地址\n"+resp);
}

