@js:
pages = java.getString(
	"em#pagestats.0@text", src);
yes = pages.match(/(\d+)$/)||[];
var page = yes[1], next =[];
if(page -1 > result.length){
	for(i = 2; i <= page; i++){
		url = baseUrl.replace(/\.html/,`/${i}`);
		next.push(url)
		}
	}else next = result;
next

exploreUrl:
@js:
sort=[];
push=(title,url,type1,type2)=>sort.push({
		title: title,
		url: url,
		style: {
				layout_flexGrow: type1,
				layout_flexBasisPercent: type2
			}
	});

fl=["玄幻","修真","都市","历史","网游","科幻","女生"];
  [["全部",""],["完结","/over"]].map([title,uri]=>{
	push('分类'+title+'', null, 1, 1);
	  fl.map((title,index)=>{
	    index++;
	    url= `${uri}/sort/${index}_{{page}}.html`;
	if(index+1 <= fl.length - fl.length%3)
			 {	push(title, url, 1, 0.25);}
				else{ push(title, url, 0, 0.29);}

    	});
	});

push('榜单', null, 1, 1);
bd=[["总排行榜","allvisit"],["月排行榜","monthvisit"],["周排行榜","weekvisit"],["收 藏 榜","goodnum"]];
bd.map(([title,uri],index)=>{
	    url= `/top/${uri}/`;
	if(index+1 <= bd.length - bd.length%2)
			 {	push(title, url, 1, 0.4);}
				else{ push(title, url, 0, 0.4);}

    	});
JSON.stringify(sort);

