ruleToc.nextTocUrl:
.page-link@a@href
@js:
next = [];
url = result[0];
length = result.length;
p = src.match(/page-link">1\/(\d+)/)||[];
page = Number(p[1]);
if(length < page){
	for(i = 2; i <= page; i++){
		link = String(url).replace(/_\d+/,`_${i}`);
		next.push(link)
		}
		next;
	}else result

