	m.push(a);
	n.push(b);
}
//clear
function c() {
	m=[];
 	n=[];	
}
//excute
function e(debug){
	for(i in n) {
		result=result.replace(m[i],n[i]);
	}
	if(debug) {
		java.log(debug);
		java.log(result);
	}
