        '全部': '*', // 允许所有模式
        '默认': ['小说']
    };

    const source = getArgument('source');
    const allowedModes = mediaConfig[source] || mediaConfig['默认'];
    let targetMedia = mediaConfig['默认'][0]; 
    let isAllowed = false;

    if (allowedModes === '*' || allowedModes.includes(media)) {
        targetMedia = media;
        isAllowed = true;
    } else if (Array.isArray(allowedModes)) {
        targetMedia = allowedModes[0]; 
