    const message = isAllowed 
        ? `\n\n已切换至：${targetMedia}\n请重新搜索书籍！`
        : `\n\n目前${source}：不支持【${media}】模式！\n已自动切换至：${targetMedia}`;

    setArgument('media', targetMedia);
    java.toast(message);
}

