if (baseUrl.match(/69shuba/)){
let o = java.getElement('.catalog:last-child>ul>li')
let aid = java.getString('aid')
if(aid!=''){
	let items = java.getElement('items')
	o = []
	for(v of Object.values(items)){
		let href = java.get('path')+aid+'/'+v.cid+java.get('tp')
		href = completeUrl(href, baseUrl)
		o.push({text:v.n,href:href})
	}
}else{
	let use = {}, offset = 0
	Array.from(o).filter(v=>v.hasAttr('data-num')).sort((a,b)=>a.attr('data-num')-b.attr('data-num')).map((v, i)=>{if(!use[v.attr('data-num')]){o[i-offset]=v.select('a')[0];use[v.attr('data-num')]=true}else{o.remove(i-offset);offset++}})
}o
}else {
	a=Array.from(java.getElements('.zjbox@ul@li@a'))
	b=a.reverse()
	}

