ruleSearch.bookList:
tr!0
<js>
result.toArray().slice(0,3).map(o=>{
    var detail = o.select('td:eq(0) a').attr('href');
    var J = org.jsoup.Jsoup.parse(java.ajax(detail));
    var oText = s => String(o.select(s).text()).trim();
    var jText = s => String(J.select(s).text()).trim();
    return {
       name:  oText('td:eq(0)'),
       author: oText('td:eq(2)'),
       tags:  [oText('td:eq(5)'),J.select('meta[property="og:novel:category"]').attr('content')].join(','),
       num: oText('td:eq(3)') + '字',
       latest: oText('td:eq(1)').replace(/(正文)?(\s+|_)/,'').replace(/[\(（【].*[求更谢乐发推].*[】）\)]/,'')+ oText('td:eq(4)').replace(/(\d+-\d+-\d+)/,'（$1）'),
       intro: J.select('meta[name="description"]').attr('content'),
       cover: J.select('meta[property="og:image"]').attr('content'),
       catalogUrl: detail
    }
});
</js>

