function encode(data){
	const { java } = this;
  return	java.createSymmetricCrypto("DES/CBC/PKCS5Padding", String("KW8Dvm2N"), String("1ae2c94b")).encryptBase64(data)
	}

function Map(e) { 
	  const { source } = this;
    let infomap = source.getLoginHeaderMap();
    let map = (infomap !== null && infomap.get(e)) ? infomap.get(e) : '';
    return String(map);
}


function setHead(k,v){
	const { source } = this;
  let  infomap = source.getLoginHeaderMap();
  let map = (infomap !== null && infomap !== "")?infomap:{};
  map[k] = v;
  source.putLoginHeader(JSON.stringify(map))
	}

function bDe(d){
	const { java } = this;
	let re,token,novelId,chapterId;
	if(/^http/.test(d)){
		token =/\d+_[\w\d]{16,}/.test(d)?"token="+ d.match(/\d+_[\w\d]{16,}/)[0]:"token=";
   novelId=d.match(/novelId=\d+/)[0];
   chapterId = /chapterId=\d+/.test(d)?"&"+d.match(/chapterId=\d+/)[0]:"";
   re =  `${token}&${novelId}${chapterId}`;
		}else{
			re = String(java.base64Decode(String(d).match(/base64,([^,]+),/)[1]))
			}
	return re
	}

function getToken(d){
	return d.match(/token=(.*?)&/)[1];
	}

function getNid(d){
	return d.match(/novelId=(\d+)/)[1];
	}

