expl = []
push = (title, url, type) => expl.push({
	title: title,
	url: url,
	style: {
		layout_flexGrow: 1,
		layout_flexBasisPercent: type
	}
})
// 默认一行3个发现项
flex = 0.25
// 获取数据
$ = checkData()
if ($.isLocal) {
	// 本地发现
	categorys = new Set()
	tags = new Set()
	status = new Set()
	pornrate = new Set()
	score = new Set()

	// 提取分类、标签、状态、肉量和评分
	for (let bookId in $.books) {
		let book = $.books[bookId]
		if (book.category) {
			book.category.forEach(cat => categorys.add(cat))
		}
		if (book.tags) {
			book.tags.forEach(tag => tags.add(tag))
		}
		if (book.status) {
			status.add(book.status)
		}
		if (book.pornrate) {
			pornrate.add(book.pornrate.replace(/[\d.\(\)%]/g, ""))
		}
		if (typeof book.score === 'number') {
			score.add(String(book.score).replace(/\.\d+/, ""))
		}
	}
	data = [
		{
			"title": `📊排行(当前按${$.sortType=="pornrate"?"肉量":($.sortType=="score"?"评分":($.sortType=="word"?"字数":($.sortType=="update"?"更新":"ID")))}${$.order?"⬆️正":"⬇️倒"}序排列)`,
			"titleUrl": "",
			"urlPattern": "{{toDataUrl('id:',page-1,'<key>')}}",
			"titles": [["🕒ID", "id"], ["💗肉量", "pornrate"], ["💯评分", "score"], ["🎖️更新", "update"], ["📃字数", "word"]]
		},
		{
			"title": `📚分类`,
			"titleUrl": "",
			"urlPattern": "{{toDataUrl('category:<key>',page-1)}}",
			"titles": Array.from(categorys)
		},
		{
			"title": `🔖标签`,
			"titleUrl": "",
			"urlPattern": "{{toDataUrl('tags:<key>',page-1)}}",
			"titles": Array.from(tags)
		},
		{
			"title": `🎐状态`,
			"titleUrl": "",
			"urlPattern": "{{toDataUrl('status:<key>',page-1)}}",
			"titles":Array.from(status)
		},
		{
			"title": `💗肉量`,
			"titleUrl": "",
			"urlPattern": "{{toDataUrl('pornrate:^[\\\\d.%]+\\\\(<key>\\\\)',page-1)}}",
			"titles": Array.from(pornrate)
		},
		{
			"title": `💯评分`,
			"titleUrl": "",
			"urlPattern": "{{toDataUrl('score:^<key>(\\\\.\\\\d+)?$',page-1)}}",
			"titles": Array.from(score).sort((a, b) => a - b)
		}
	]
	// 过滤没有标签的项
	data = data.filter(d => d.titles.length>0)
}else{
	// 使用在线发现
	data=JSON.parse(java.base64Decode(""))
}
// 构造发现数组
data.forEach(o => {
	push(o.title, o.titleUrl, 1)
	let flexBasisPercent = flex
	let flag = true
	if(o.titles.length == 2 || o.titles.length == 4) {
		flexBasisPercent=0.4
		flag=false
	}
	o.titles.map((t, index) => {
		t = Array.isArray(t) ? t : [t]
		push(String(t[0]).replace(/^(超多肉)$/,"$1(>=60%)").replace(/^(多肉)$/,"$1(40%~60%)").replace(/^(中肉)$/,"$1(20%~40%)").replace(/^(少肉)$/,"$1(<20%)"), o.urlPattern.replace(/<index>/,index+1).replace(/<key>/,t[t.length-1]), flexBasisPercent)
	})
	if (flag) {
		Array.from({ length: [0, 2, 1][o.titles.length % 3] }, () => push("", "", flexBasisPercent))
		}
})
java.toast(`\n✅发现刷新成功✅\n共${expl.filter(e=>e.url!="").length}个类别`)
JSON.stringify(expl)

