	result=org.jsoup.Jsoup.parse(java.ajax(source.key))
	hrefs=result.select(".chapter-new li a.name")
	hrefs.sort((a,b)=>b.attr("href").match(/\d+/)-a.attr("href").match(/\d+/))
	lastestId=Number(hrefs.attr("href").match(/\d+/)[0])
	$.time=time
	$.lastestId=lastestId
