let su = "https://yaoluxs.com";

function cpo(nu) {
  const { java, source } = this;
  return java.base64Encode(nu || source.getKey(), 3);
}

function getServerId() {
  return String(Math.floor(Math.random() * 100) + 100);
}

function getHost() {
  const { java, cache } = this;
  let maxRetries = 10;
  let url = 'https://143.244.204.138/requests?fso=';

  for (let i = 0; i < maxRetries; i++) {
    try {
      let sid = getServerId();
      java.log('测试服务器：' + sid);
      let body = 'url=' + encodeURIComponent(url) + '&proxyServerId=' + sid + '&frontOrigin=https%3A%2F%2Fwww.croxy.network';
      let response = java.post(url, body, {});

      let locationHeader = response.header('location');
      if (locationHeader) {
        let u = java.toURL(locationHeader);
        let host = 'https://' + u.host;
        let cook = '__cpc=' + u.searchParams.get('s');
        let time = Date.now().toString();

        cache.put('croxy ck', cook);
        cache.put('croxy time', time, 7200);
        cache.put('croxy host', host);
        return host;
      }
    } catch (e) {
      if (i === maxRetries - 1) {
        throw new Error('达到最大重试次数，无法获取服务器信息');
      }
    }
  }

  throw new Error('接口获取错误');
}

function ipCheck() {
  const { java, cache } = this;

  let time = cache.get('croxy time');
  let ntime = Date.now();
  let timeDiff = time ? ntime - parseInt(time, 10) : Infinity;

  if (!cache.get('croxy host') || !time || timeDiff > 7200000) {
    Packages.java.lang.Thread.sleep(Math.floor(Math.random() * 800));

    if (!cache.get('cxylock')) {
      try {
        cache.put('cxylock', 1, 60);
        this.getHost();
      } catch (error) {
        java.log('未知错误');
      } finally {
        cache.delete('cxylock');
      }
    }

    while (cache.get('cxylock')) {
      Packages.java.lang.Thread.sleep(100);
    }
  }
  return String(cache.get('croxy host'));
}

