ruleToc.chapterList:
@js:var data = JSON.parse(result);
volume = data.data.chapterListWithVolume;
var flattenChapters = function (chapters) {
    let flatArray = [];
    for (let chapter of chapters) {
        if (Array.isArray(chapter)) {
            flatArray = flatArray.concat(flattenChapters(chapter));
        } else {
            flatArray.push(chapter);
        }
    }
    return flatArray;
}
var list = flattenChapters(volume);
list.forEach(item => item.itemId = `https://fanqienovel.com/api/reader/full?itemId=${item.itemId}&force_mobile=0`)

list;

