        if (withTag) {
            url += "&withTag=" + withTag; // 添加 withTag 部分
        }
        result.push({
            title: key,
            url: url,
            style: { layout_flexGrow: 1, layout_flexBasisPercent: 1 }
        });
        for (var tagKey in other_tags) {
