let replaceImg=function(e,a,b){
	let regex = new RegExp('<img src[^>]+/' + a+ '\.jpg">', 'g')
	return e.replace(regex,b)
	}
function x(e,m){
	for (i in m){
		e=replaceImg(e,i,m[i])
		}
	return e
	}

