function getContent($) {
    let galleryItems = "",videos="";

    let long_imgs = ($.pic_num??0)?($.pics??[]):[];
    let long_video = $.page_info?.type==="video"?$.page_info:{};

    if(long_imgs.length){
        galleryItems = long_imgs.map((img, i) => `
        <div class="gallery-item">
            <img src="${img.url}" data-original="${img?.large?.url}" loading="lazy">
            <div class="zoom-icon">üîç</div>
        </div>`).join('');
       galleryItems = `<div class="image-gallery">${galleryItems}</div>`;
    }
    if(JSON.stringify(long_video)!=="{}"){
        if(long_video.urls){
        long_video = long_video.urls;
        videos = long_video.hevc_mp4_hd || long_video.mp4_720p_mp4 || long_video.mp4_hd_mp4 || long_video.mp4_ld_mp4
        }else{
        long_video = long_video.media_info;
        videos = long_video.stream_url_hd || long_video.stream_url
        }

        videos = `<div class="media-container"><video src="${videos}" controls poster="${$.page_info.page_pic.url}"></video></div>`
    }

    return `
      <div class="comment-item">
        <div class="comment-user">
          <img class="user-avatar" src="${$.user.profile_image_url}">
          <div class="user-info">
            <span class="user-name">${$.user.screen_name}</span>
