function formatTime(dateStr) {
    const date = new Date(dateStr);
    const year = date.getFullYear();
    const month = date.getMonth() + 1; 
    const day = date.getDate();
    const hours = date.getHours().toString().padStart(2, '0');
    const minutes = date.getMinutes().toString().padStart(2, '0');

    return `${year}å¹´${month}æœˆ${day}æ—¥ ${hours}:${minutes}`;
}
function formatCount(count) {
  if (count < 10000) return count.toString();  
  const inWan = (count / 10000).toFixed(2);
  return inWan.endsWith('.00') 
    ? `${inWan.split('.')[0]}ä¸‡` 
    : `${inWan}ä¸‡`;
}

function getContent($) {
    let galleryItems = "",videos="";
    let long_imgs = ($.pic_num??0)?($.pics??[]):[];
    let long_video = $.page_info?.type==="video"?$.page_info:{};

    if(long_imgs.length){
        galleryItems = long_imgs.map((img, i) => `
        <div class="gallery-item">
            <img src="${img.url}" data-original="${img?.large?.url}" loading="lazy">
            <div class="zoom-icon">ğŸ”</div>
        </div>`).join('');
       galleryItems = `<div class="image-gallery">${galleryItems}</div>`;
    }
    if(JSON.stringify(long_video)!=="{}"){
        if(long_video.urls){
        long_video = long_video.urls;
        videos = long_video.hevc_mp4_hd || long_video.mp4_720p_mp4 || long_video.mp4_hd_mp4 || long_video.mp4_ld_mp4
        }else{
        long_video = long_video.media_info;
        videos = long_video.stream_url_hd || long_video.stream_url
        }

        videos = `<div class="media-container"><video src="${videos}" controls poster="${$.page_info.page_pic.url}"></video></div>`
    }

    return `
      <div class="comment-item">
        <div class="comment-user">
          <img class="user-avatar" src="${$?.user?.avatar_hd}" onclick="window.open('https://m.weibo.cn/n/${$?.user?.screen_name}')">
          <div class="user-info">
            <span class="user-name" onclick="window.open('https://m.weibo.cn/n/${$?.user?.screen_name}')">${$?.user?.screen_name}</span>
            <span class="comment-time">${formatTime($.created_at)}</span>
          </div>
          <div class="like_counts"><span>â™¡${formatCount($.attitudes_count)}</span></div>
        </div>
        <div class="comment-text">${$.text.replace(/https?:\/\/m.weibo.cn\/\d+\/(\d+)/g,'https://m.weibo.cn/a/$1').replace(/'\/n\//g,"'https://m.weibo.cn/n/")}</div>
        ${videos}
        ${galleryItems}
      </div>`;
}

