    var line = lines[i].trim();
    if (line === '') continue;

    // å¦‚æœä»¥ â— å¼€å¤´ï¼Œåˆ™æ˜¯åˆ†ç»„å
    if (line.startsWith('â—')) {
        // å¦‚æœå½“å‰åˆ†ç»„åå’Œå†…å®¹å·²å­˜åœ¨ï¼Œä¿å­˜ä¸Šä¸€ä¸ªåˆ†ç»„
        if (groupName && content) {
            var encodedContent = java.base64Encode(content); // ä½¿ç”¨ java.base64Encode
            result.push(`${groupName}::data:;base64,${encodedContent},{"type":""}`);
        }
        groupName = line.slice(1, -1).trim(); // å»æ‰ â—ï¼Œè®¾ç½®æ–°çš„åˆ†ç»„å
        content = ''; // é‡ç½®å†…å®¹
    } else if (line.startsWith('ã€')) {
        // å¦‚æœä»¥ ã€ å¼€å¤´ï¼Œåˆ™æ˜¯å†…å®¹çš„ä¸€éƒ¨åˆ†
        content += line + '\n';
    }
}

// ä¿å­˜æœ€åä¸€ä¸ªåˆ†ç»„
if (groupName && content) {
    var encodedContent = java.base64Encode(content); // ä½¿ç”¨ java.base64Encode
