jsLib:
function GetUrl(link_Params,link_Urlpath){
 	  const { java,source } = this;    
 	  burl = source.getKey().match(/(http[s]?:\/\/[\.a-zA-Z0-9_-]+)/)[1];
 	  api_Params = {"time": Date.now()}

function sign(body){
		sb = "7b7fpld4roey0e6e&" + body;
		return java.md5Encode(sb);
	}
function body($){
		$ = Object.assign(api_Params, $);
		return Object.keys($).sort()
		.map(key=>key+"="+$[key]).join('&');
	}
  api_Params.sign = sign(body(link_Params));

  url =  burl + link_Urlpath + "," + JSON.stringify({
	   	"method": "POST",
	   	"body": Object.keys(api_Params).sort()
		.map(key=>key+"="+api_Params[key]).join('&')
	});
return url;
	}


function unicodebytesToChars(bArr) {
	if (bArr != null) {
		try {
			if (bArr.length != 0) {
				length = bArr.length / 2;
				textBlockArr = "";
				for (i = 0; i < length; i++) {
					i2 = i * 2;
					b = bArr[i2];
					b2 = bArr[i2 + 1];
					if (b == -1 && b2 == -2) {
						textBlockArr += "";
					} else if (b == 13 && b2 == 0) {
						textBlockArr += "";
					} else {
						textBlockArr += String.fromCharCode(((b2 << 8) & 65280) | (b & 255));
					}
				}
				return textBlockArr;
			}
		} catch(e) {	}
	}
	return null;
}

