@js:
function transformUrlToImage(url) {
    // Extract the book ID from the URL using a regular expression
    const match = url.match(/(\d+)\//);
    if (!match) {
        return 'Invalid URL';
    }

    const bookId = match[1]; // The book ID extracted from the URL

    // Calculate the prefix
    const prefix = bookId.length <= 3 ? '0' : bookId.slice(0, bookId.length - 3); // If the bookId is 3 digits or less, prefix should be '0'

    // Construct the image URL
    const imageUrl = `https://www.htxsw.com/files/article/image/${prefix}/${bookId}/${bookId}s.jpg`;

    return imageUrl;
}


result =transformUrlToImage(result)

