const encryptKeys = {
  headers: {
    QDSign: {
      key: "{1dYgqE)h9,R)hKqEcv4]k[h",
      iv: "01234567"
    },
    QDInfo: {
      key: "0821CAAD409B84020821CAAD",
      iv: String.fromCharCode(0, 0, 0, 0, 0, 0, 0, 0)
    }
  }
}

function getDeviceId(java, source) {
  if (!java || !source) {
    java = this.java
    source = this.source
  }
  let sVar = JSON.parse(!Packages.android.text.TextUtils.isEmpty(source.getVariable()) ? source.getVariable() : "{}")
  if (!sVar.deviceid) {
    let temp = ""
    let chr = "0123456789abcdef".split("")
    for (let i = 0; i < 16; i++) { temp = temp + chr[Math.floor(Math.random() * chr.length)]}
    java.log(temp)
    sVar.deviceid = temp
    source.setVariable(JSON.stringify(sVar, null, 2))
    return temp
  }
  return sVar.deviceid
}

function QDSign(path, params, page, dmode, useCookie) {
  /*
     为保护作者人身氨醛，本函数不包含付费正文的核心请求头检查，只有基础过检测
    	 基本够正常使用，如果你有实力可以把其他头(gorgon一类的)自己补全
  */
  const { java, source } = this
  let a = params.sort().join("&").replace(/\$page/gi, page)
  let t = Date.now()

  // Sign参数
  let sign = "Rv1rPTnczce|" + t + "||||||" + java.md5Encode(a.toLowerCase())
  let Sign = java.tripleDESEncodeBase64Str(sign, encryptKeys.headers.QDSign.key, "CBC", "PKCS5Padding", encryptKeys.headers.QDSign.iv)

  // Info参数
  let id = getDeviceId(java, source)
  let info = id + "||||||1||999|" + t
  let Info = java.tripleDESEncodeBase64Str(info, encryptKeys.headers.QDInfo.key, "CBC", "PKCS5Padding", encryptKeys.headers.QDInfo.iv)

  let ck = ""
  if (useCookie) {
    let header = getLoginHeader(source)
    ck = `ywguid=${header.guid}; ywkey=${header.key}`
  }

  if (dmode) {
    return {
      url: "https://druidv6.if.qidian.com/argus/api/" + path + "?" + a,
      headers: {
        QDSign: Sign,
        QDInfo: Info,
        tstamp: String(t),
        Cookie: ck
      }
    }
  }
  // Url拼接
  return java.log("https://druidv6.if.qidian.com/argus/api/" + path + "?" + a + "," + JSON.stringify({
    headers: {
      QDSign: Sign,
      QDInfo: Info,
      tstamp: String(t),
      Cookie: ck
    }
  }))
}

var ji = new JavaImporter()
ji.importPackage(Packages.okhttp3)
with(ji) {
  function getBytes(url, headers) {
    let client = new OkHttpClient()

    let requestBuilder = new Request.Builder()
      .url(url)
      .get()

    // 添加请求头
    for (let headerName in headers) {
      requestBuilder.addHeader(headerName, headers[headerName])
    }

    let request = requestBuilder.build()
    let response = client.newCall(request).execute()

    try {
      // 处理响应
      let responseBody = response.body().bytes()
      return responseBody
    } finally {
      response.close()
    }
  }
}

function dContentQD(cid, body) {
  const { java, source } = this
  let aid = getDeviceId(java, source)
  let sha1 = "0" + aid + cid + "2EEE1433A152E84B3756301D8FA3E69A"
  let key0 = java.HMacBase64(sha1, "HMAC-SHA1", aid).slice(0, -4)
  let key1 = java.base64Decode("BjY2NjY2NjY2NjY2NjY2NjY2NjY2NjY2NjY2NjY2NjY2NjY2NjY2NjY2NjY2NjY2NjY2NjY2NjY2NjY2NjY2Ng==")
  let key2 = java.md5Encode(key1 + key0 + aid)
  let key3 = java.hexDecodeToByteArray("6c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c5c" + key2)

  let md5 = Packages.cn.hutool.crypto.digest.DigestUtil.md5(key3)
  let B = Packages.android.util.Base64
  let key = B.encodeToString(md5, B.NO_WRAP)
  let iv = java.base64Decode("AAAAAAAAAAA=")

  return java.createSymmetricCrypto("DESede/CBC/PKCS5Padding", key0, iv).decryptStr(java.createSymmetricCrypto("DESede/CBC/PKCS5Padding", key, iv).decrypt(Array.from(body).slice(8, -16)))
}

