@js:
function secret(data, code) {
    code = java.md5Encode(code);
    iv = code.slice(0, 16);
    key = code.slice(16);
    return java.createSymmetricCrypto("AES/CBC/PKCS7Padding", key, iv).decryptStr(data);
}

let match = src.match(/var chapter = secret\(['"](.+?)['"],\s*['"](.+?)['"].*\)/);
if (match) {
    result = secret(match[1], match[2]);
}

