function x(encryptedData, key, iv) {
  const ciphertext = CryptoJS.enc.Base64.parse(encryptedData);
  const ivBytes = CryptoJS.enc.Base64.parse(iv);
  const keyBytes = CryptoJS.enc.Utf8.parse(key);
