function setupAutoRemoveImages() {
    const AD_SELECTORS = [
        'ut1_img',
        '.ut1_img_content',
        '.ut1_img utactivity',
        '.ut1_img_content_js',
        '.ut1_img_ex_content',
        '.di_img',
        '.di_100',
        '[class^="mvspan"]'
    ].join(',');
    // 使用防抖函数优化高频DOM操作
    let cleanupTimer;
    const debouncedRemove = () => {
        clearTimeout(cleanupTimer);
        cleanupTimer = setTimeout(removeAllImages, 200);
    };
    const removeAllImages = () => { document.querySelectorAll(AD_SELECTORS).forEach(img => img.remove()); };
//forEach方法的回调函数是表达式，不是语句，不能加分号。

    const observer = new MutationObserver(mutations => {
        // 变化有效性检查
        const hasValidMutation = mutations.some(mutation =>
            mutation.type === 'childList' &&
            mutation.addedNodes.length > 0
        );

        if (hasValidMutation) {
            debouncedRemove();
        }
    });

    // 监听
    const observerConfig = {
        childList: true,
        subtree: true
        // 备用attributes: true
    };

    // 初始化立即执行一次清理
    removeAllImages();

    // 启动观察
    observer.observe(document.body, observerConfig);

    window.addEventListener('error', e => {
        console.error('广告清理出错:', e.error);
        observer.disconnect();
    });
}

// 自动重试机制
let retryCount = 0;
function init() {
    try {
        setupAutoRemoveImages();
    } catch (e) {
        if (retryCount++ < 3) {
            setTimeout(init, 1000 * retryCount);
        }
    }
}

// 确保DOM加载后执行
if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', init);
} else {
    init();
}

