//java.md5Encode hex_md5 str_md5
    var params ='&bookId=' + bid + '&user_id=' + userId + '&timestamp=' + timestamp + '&sign=' + sign ;

    let e = { cata: 'http://content.shuqireader.com/openapi/book/chapterlist?shuqi_h5=205&platform=0'+ params };
    Object.assign(aladdin, e);

    list.push(aladdin);
    list;

