var next=[],
regex=/(pageNum=)(\d+)/,
num=baseUrl.match(regex)[2],
page=JSON.parse(src).totalPage;
if(page>num){
	for(i=1;i<=page;i++){
		url=baseUrl.replace(regex,
			`$1${i}`);
			next.push(url);
		}
	next
	}

