$.data[*]
<js>
(() => {
	list = Array.from(result).filter(item => item.type == "book")
	return list
})()

bookList = list.map(x => `https://api.wan123x.com/read/getBookDetail?book_id=${x.id}`)

d = java.ajaxAll(bookList).map(x => x.body());

$=(b,c)=>java.getString(b,c);

Array.from(list,(a,i)=>{
	list={
		name:$("name",a),
		author:$("$..author_name",d[i]),
		kind:$("$..category1&&$..category2&&$..role&&$..ability",d[i]),
		words:$("$..words",d[i]),
		lastc:$("",d[i]),
		intro:"&nbsp;&nbsp;" + $("$..intro",d[i]),
		cover:$("$..cover_url",d[i]),
		link:`https://api.wan123x.com/read/getCatalog?book_id=${$("$..book_id",d[i])}&catalog_order=asc`
		};
		return list
	});
</js>

