ruleContent:
id.msacwl-slider-1@tag.img@data-lazy##(.*)
<pre>
    <js>
        imgList = result.split("\n");
        imgDom = "";
        for (let i = 0; i < imgList.length; i++) {
          imgDom += `<img src="http://iph.href.lu/375x400?text=AiKan" data-src="${imgList[i]}" />`;
        }
      </js>
</pre>
<meta name="viewport" content="width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=0" />
<h3>{{@@class.entry-title.0@text}}</h3>
<p>时间：{{@@class.updated.0@text}}</p>
<p>{{@@class.entry-content@tag.p.[0:4]@text}}</p>
<div id="imgbox">{{result}}</div>
<!-- tag.iframe.0@data-src -->
<iframe src="{{@@tag.iframe.0@data-src}}"></iframe>
<link rel="stylesheet" href="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.1/viewer.min.css" />
<script src="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.1/viewer.min.js"></script>
<script>
  root = document.getElementById("imgbox");
  //查看大图插件
  new Viewer(root, {
    title: false,
    interval: 3000,
  });
  lazy = new IntersectionObserver(
    (entires) => {
      console.log("entires", entires);
      entires.forEach((item) => {
        if (item.isIntersecting) {
          item = item.target;
          item.src = item.getAttribute("data-src");
          lazy.unobserve(item);
        }
      });
    },
    {
      root: root,
      threshold: [0],
      rootMargin: "0px 0px 500px 0px",
    }
  );

  imgs = document.getElementsByTagName("img");
  Array.from(imgs).forEach((item) => {
    lazy.observe(item);
  });
</script>

