// 从页面链接规律提取当前页码（假设URL格式固定为page/X）
var doc = org.jsoup.Jsoup.parse(result);
// 尝试从下一页链接反推当前页码（如下一页是page/3，则当前是2）
var nextLinkElem = doc.select("div.mac_pages a[title='下一页']").first();
if (!nextLinkElem) {
    ""; // 无下一页，停止
}
var nextUrl = nextLinkElem.attr("href");
var pageMatch = nextUrl.match(/page\/(\d+)/);
if (!pageMatch) {
    ""; // 未找到页码格式，停止
}
var nextPage = parseInt(pageMatch[1], 10);
var currentPage = nextPage - 1; // 反推当前页码

// 控制翻到page/10后停止（即下一页是page/11时停止）
if (nextPage <= 50) {
    nextUrl; // 下一页≤10，继续
} else {
    ""; // 下一页>10，停止
}

