						devices = JSON.parse(res.device).length;
					} catch (e) {
						devices = res.device ? 1 : 0;
					}
					let isVip;
					if (res.is_vip==1) {
						isVip = 'VIP';
					} else if (res.is_vip>=2) {
						isVip = 'SVIP';
					} else {
						isVip = '普通会员';
					}
					tips= `
┏┅┅┅┅┅┅┱┄┄┄┄┄┄┄┄┄┄┐
　✉️邮箱　　　　${res.email.replace(/(.{3}).*?@/,"$1***@").padEnd(20,"\t")}
┣┅┅┅┅┅┅╉┄┄┄┄┄┄┄┄┄┄┤
　🔑密钥　　　　${(`${res.user_key.substring(0,4)}***${res.user_key.slice(-4)}`).padEnd(20,"\t")}
┣┅┅┅┅┅┅╉┄┄┄┄┄┄┄┄┄┄┤
　📅注册时间　　${java.timeFormat(res.register_time*1000).padEnd(20,"\t")}
┣┅┅┅┅┅┅╉┄┄┄┄┄┄┄┄┄┄┤
　🗒️今日阅读　　${(java.timeFormat(new Date()).slice(0,10)==java.timeFormat(res.last_read_time * 1000).slice(0,10)?res.day_read_count:0).toString().padEnd(20,"\t")}
┣┅┅┅┅┅┅╉┄┄┄┄┄┄┄┄┄┄┤
　📚累计阅读　　${res.all_read_count.toString().padEnd(20,"\t")}
┣┅┅┅┅┅┅╉┄┄┄┄┄┄┄┄┄┄┤
　🕓最后阅读　　${(res.last_read_time != 0?java.timeFormat(res.last_read_time * 1000):'未阅读').padEnd(20,"\t")}
┣┅┅┅┅┅┅╉┄┄┄┄┄┄┄┄┄┄┤
　📱关联设备　　${devices.toString().padEnd(20,"\t")}
┣┅┅┅┅┅┅╉┄┄┄┄┄┄┄┄┄┄┤
　👑会员状态　　${isVip.padEnd(20,"\t")}
┣┅┅┅┅┅┅╉┄┄┄┄┄┄┄┄┄┄┤
　🚫封禁状态　　${res.is_banned?'已封禁':'正常　'}　　　　　　　
┗┅┅┅┅┅┅┹┄┄┄┄┄┄┄┄┄┄┘
`
					java.log(tips)
					java.longToast(tips)
				} else {
					throw new Error(res.msg)
				}
			} catch(e) {
				//java.log(e)
				java.toast("\n检测登录失败\n"+e.message)
			}
}

function clearDevice() {
	let base_url = getArgument('server')
	let res=JSON.parse(java.ajax(`${base_url}/clear,{"method":"POST"}`))
