loginUrl:
ul = source.key;
function reg() {
java.startBrowser("https://alicesw.org/user/user/reg.html","注册账号");
	}
function login() {}
function Login() {
	cookie.removeCookie(ul);
	loginul = "https://alicesw.org/user/user/login.html"
	codeul = "https://alicesw.org/user/user/verify.html"

	na = source.getLoginInfoMap()['用户名/邮箱'];
	pa = source.getLoginInfoMap()['密码']
	if (String(na&&pa)) {
	phpid = java.get(codeul,{}).header("set-cookies");
	java.log(phpid);
	code = java.getVerificationCode(codeul);
	body = `username=${na}&password=${pa}&code=${code}`
cook = java.post(loginul,body,{"x-requested-with":"XMLHttpRequest","Cookie": phpid });
ck = String(cook.cookies()).replace(/[{}]/g,"").replace(",",";");
	java.log(String(ck))
	headers = JSON.stringify({
		       "Cookie": ck
		    });
		usercode = JSON.parse(cook.body()).msg
		java.toast(usercode);
		if (ck) {
source.putLoginHeader(headers);
cookie.replaceCookie(ul,ck);
} else {
	source.removeLoginHeader();
	cookie.removeCookie(ul)
	}
	    } else {
			java.longToast("❌\n未填入账号数据\n请填入后点击上方√再点击登录按钮");
			}
}
function out() {
  source.removeLoginHeader();
  cookie.removeCookie(ul);
java.toast("已退出登录状态");
}
function show() {
	jc = source.getLoginHeaderMap();
	if (jc) {
	info = jc.get("Cookie").match(/%22%3A%22(\d+?)%22%2C%22username%22%3A%22(.*?)%22%7D/)
	us = info[2];
	id = info[1];
	codes = "✔️已登录";
	} else {
		us = "无";
	id = "无";
	codes = "❌未登录";
}
txt = `\n◎用户：${us}◎\n◎用户ID：${id}◎\n◎登录状态：${codes}◎`
	java.longToast(txt);
}

ruleToc.chapterList:
@js: flags = result.match(/chapters/g);
//判断是否存在目录
if (flags == null) {
    var list = [];
    list.push({
        m: baseUrl,
        n: "正文"
    });
    list;
} else {
    var regex = /<div class="chapters">([\s\S]*?)上一章/i;
    var matches = regex.exec(result);
    if (matches) {
        var content = matches[1];
    }
    var regex = /<a href="([^"]+)"><i class="fa fa-book"><\/i>([^<]+)/g;
    var list = [];
    var match;
    while ((match = regex.exec(content)) !== null) {
        list.push({
            m: match[1],
            n: match[2]
        });
    }
}
list

