url;

exploreUrl:
@js:
expl = []
push = (title, url, type) => expl.push({
	title: title,
	url: url,
	style: {
		layout_flexGrow: 1,
		layout_flexBasisPercent: type
	}
})
// 默认一行3个发现项
flex = 0.25
// 获取数据
$ = checkData()
if ($.isLocal) {
	// 本地发现
	categorys = new Set()
	tags = new Set()
	status = new Set()
	pornrate = new Set()
	score = new Set()

	// 提取分类、标签、状态、肉量和评分
	for (let bookId in $.books) {
		let book = $.books[bookId]
		if (book.category) {
			book.category.forEach(cat => categorys.add(cat))
		}
		if (book.tags) {
			book.tags.forEach(tag => tags.add(tag))
		}
		if (book.status) {
			status.add(book.status)
		}
		if (book.pornrate) {
			pornrate.add(book.pornrate.replace(/[\d.\(\)%]/g, ""))
		}
		if (typeof book.score === 'number') {
			score.add(String(book.score).replace(/\.\d+/, ""))
		}
	}
	data = [
		{
			"title": `📊排行(当前按${$.sortType=="pornrate"?"肉量":($.sortType=="score"?"评分":($.sortType=="word"?"字数":($.sortType=="update"?"更新":"ID")))}${$.order?"⬆️正":"⬇️倒"}序排列)`,
			"titleUrl": "",
			"urlPattern": "{{toDataUrl('id:',page-1,'<key>')}}",
			"titles": [["🕒ID", "id"], ["💗肉量", "pornrate"], ["💯评分", "score"], ["🎖️更新", "update"], ["📃字数", "word"]]
		},
		{
			"title": `📚分类`,
			"titleUrl": "",
			"urlPattern": "{{toDataUrl('category:<key>',page-1)}}",
			"titles": Array.from(categorys)
		},
		{
			"title": `🔖标签`,
			"titleUrl": "",
			"urlPattern": "{{toDataUrl('tags:<key>',page-1)}}",
			"titles": Array.from(tags)
		},
		{
			"title": `🎐状态`,
			"titleUrl": "",
			"urlPattern": "{{toDataUrl('status:<key>',page-1)}}",
			"titles":Array.from(status)
		},
		{
			"title": `💗肉量`,
			"titleUrl": "",
			"urlPattern": "{{toDataUrl('pornrate:^[\\\\d.%]+\\\\(<key>\\\\)',page-1)}}",
			"titles": Array.from(pornrate)
		},
		{
			"title": `💯评分`,
			"titleUrl": "",
			"urlPattern": "{{toDataUrl('score:^<key>(\\\\.\\\\d+)?$',page-1)}}",
			"titles": Array.from(score).sort((a, b) => a - b)
		}
	]
	// 过滤没有标签的项
	data = data.filter(d => d.titles.length>0)
}else{
	// 使用在线发现
	data=JSON.parse(java.base64Decode(""))
}
// 构造发现数组
data.forEach(o => {
	push(o.title, o.titleUrl, 1)
	let flexBasisPercent = flex
	let flag = true
	if(o.titles.length == 2 || o.titles.length == 4) {
		flexBasisPercent=0.4
		flag=false
	}
	o.titles.map((t, index) => {
		t = Array.isArray(t) ? t : [t]
		push(String(t[0]).replace(/^(超多肉)$/,"$1(>=60%)").replace(/^(多肉)$/,"$1(40%~60%)").replace(/^(中肉)$/,"$1(20%~40%)").replace(/^(少肉)$/,"$1(<20%)"), o.urlPattern.replace(/<index>/,index+1).replace(/<key>/,t[t.length-1]), flexBasisPercent)
	})
	if (flag) {
		Array.from({ length: [0, 2, 1][o.titles.length % 3] }, () => push("", "", flexBasisPercent))
		}
})
java.toast(`\n✅发现刷新成功✅\n共${expl.filter(e=>e.url!="").length}个类别`)
JSON.stringify(expl)

jsLib:
// 读取并返回数据
function checkData(_) {
	const { java, source, cache }= _ || this
	java.log("checkData()")
	const vName = `sourceVariable_${source.getKey()}`
	let temp
	try{
		temp = []
		// 尝试从缓存中分段读取数据
		for(let i = 0; i < cache.get(`${vName}_iLength`);){
			temp.push(cache.get(`${vName}_${i}`))
			i += 500000
		}
		temp = temp.join("")
		// 如果没有数据，尝试从缓存文件或源变量中获取
		if (!temp||temp=="") {
			temp=cache.getFile(vName)||cache.get(vName)
		}
		// 解析 JSON 数据
		const $=JSON.parse(temp)
		// 验证数据结构
		if (typeof $.books!='object') {
			throw new Error("错误数据")
		}
		return $
	} catch (e) {
		java.log(`function checkData Error:${e}`)
		return reset(false, false, this)
	}
}
// 保存数据
function setv(json, _, isString) {
	const { java, source, cache }= _ || this
	const vName = `sourceVariable_${source.getKey()}`
	java.log("setv()")
	// 如果输入不是字符串，则将其转换为 JSON 字符串
	if (!isString) {
		json = JSON.stringify(json)
	}
	java.logType(json)
	java.log(`json length: ${json.length}`)
	// 记录数据的长度
	cache.put(`${vName}_iLength`,json.length,0)
	// 分段存储数据
	for (let i = 0; i < json.length;) {
		cache.put(`${vName}_${i}`,json.slice(i, i += 500000),0)
	}
}
// 重置数据
function reset(check, showTip, _) {
	const { java, source, cache }= _ || this
	let vName = String(`sourceVariable_${source.getKey()}`)
	java.log("reset()"+check+showTip)
	if(check){
		try{
			check = java.getVerificationCode(checkImg)
		}catch(e){}
	} else {
		check = "y"
	}
	if (check == "y") {
	let $ = {
		api:2,
		time:20250204,
		lastestId:11759,
		sum:50,
		isLocal:false,
		order:false,
		errorIds:[],
		sortType:"",
		concurrency:1,
		books:{}
		}
	for (i = 0; i < cache.get(`${vName}_iLength`);) {
		cache.delete(`${vName}_${i}`)
		i += 500000
	}
	cache.delete(vName)
	cache.delete(`${vName}_iLength`)
	setv($, _||this)
	if (showTip) {
	java.toast('\n✅重置成功，请重新构造本地数据')
	}
	return $
	} else {
		java.toast("\n❎用户取消重置❎")
	}
}
function getl(arr, page){
	return arr.slice(page*500, (+page+1)*500)
}
function toDataUrl(key, page, sortType) {
	const { java } = this
	let data = [key, page, sortType]
	return `data:;base64,${java.base64Encode(data.join("|==|"))},{"type":""}`
}
function sleep(t) {
    Packages.java.lang.Thread.sleep(t)
}
function createRegExp(input) {
    const flagsRegex = /\(\?([igm]*)\)(?!\))/g;
    let flags = '';
    let pattern = input;
    let match;
    while ((match = flagsRegex.exec(input)) !== null) {
        flags += match[1];
        pattern = pattern.substring(0, match.index) + pattern.substring(match.index + match[0].length);
    }
    return new RegExp(pattern, flags);
}
function getSortValue(book, type) {
	let data = book[type]
	let value
	switch (type) {
		case 'pornrate':
			value = data.split("%")[0]
			break;
		case 'update':
			value = parseInt(data.replace(/-/g,""))
			break;
		case 'word':
			m = data.match(/[^\d.]+/)
			f = 1
			if (m&&/k/i.test(m)) {
				f = 1000
			}else if(m&&/m/i.test(m)) {
				f = 1000000
			}
			value = Number(data.match(/[\d.]+/))*f
			break;
		default:
			value = data
	}
	return value
}
function spArr(arr, num) {
	let newArr = []
	for(let i = 0; i < arr.length;){
		newArr.push(arr.slice(i, i += num))
	}
	return newArr
}
