   		let v =arguments[i].split("=")[1];
   		map[k] = v;
   		}
  source.putLoginHeader("#"+JSON.stringify(map))
	}

function Map(e,that) { 
	  const { source } = that || this;
   return getHeaderMap(that||this)[e]??""
}

