async function loadComments(options = {}) {
  const {
    id = weiboId,
    isReply = false,
    isLoadMore = false,
    targetElement = isReply ? '#reply-list' : '#container',
    tElement = isReply ? '#reply-list .comments-list' : '.comments-list',
    containerTemplate = null,
    sort = isReply ? repSortType : sortType
  } = options;


  const loadingFlag = isReply ? repLoading : isLoading;
  if (loadingFlag) return;
  isReply ? repLoading = true : isLoading = true;


  let url;
  if (isReply) {
    url = \`https://weibo.com/ajax/statuses/buildComments?flow=\${sort}&is_reload=1&id=\${id}&is_show_bulletin=2&is_mix=1&fetch_level=1&max_id=\${isLoadMore ? repCurrentMaxId : 0}&count=20&locale=zh-CN\`
  } else {
   url =\`https://weibo.com/ajax/statuses/buildComments?flow=\${sort}&is_reload=1&id=\${id}&is_show_bulletin=2&is_mix=0&count=20&fetch_level=0&locale=zh-CN\`;


    //url = \`https://m.weibo.cn/comments/hotflow?id=\${id}&mid=\${id}&max_id_type=\${sort}\`;
    if (isLoadMore && currentMaxId) {
      url += \`&max_id=\${currentMaxId}\`;
    }
  }

  try {
    const response = await fetch(url, {
      headers: {
        'Accept': 'application/json',
        'X-Requested-With': 'XMLHttpRequest'
      },
      credentials: 'include'
    });

    const data = await response.json();


    if (data.ok === -100) {
      if (/error-message/.test(document.querySelector(targetElement).innerHTML)) {
        throw new Error("éœ€è¦ç™»å½•");
      }
      document.querySelector(targetElement).innerHTML += \`
        <div class="error-message">
          è¯„è®ºåŠ è½½å¤±è´¥:è¯·<a href="\${data.url}">è®¿é—®é¦–é¡µæˆ–ç™»å½•</a>åé‡è¯•
        </div>
      \`;
      throw new Error("éœ€ç™»å½•");
    }


    const currentIdArray = isReply ? repMaxId : MaxId;
    const newMaxId = data.max_id;

    if (currentIdArray.includes(newMaxId)) {
      throw new Error("æ²¡æœ‰æ›´å¤šå†…å®¹äº†");
    }

    if (isReply) {
