          <span class="close-btn" onclick="this.closest('.reply-modal').remove();repMaxId=[];repSortType=0;repCurrentMaxId=null">Ã—</span>
        </div>
      </div>
      <div class="modal-body" id="reply-list"></div>
    </div>
  \`;
  document.body.appendChild(modal);
  modal.addEventListener('click', function(e) {

  if (e.target === modal) {
    modal.remove();
    repMaxId = [];
    repSortType = 0;
    repCurrentMaxId = null;
  }
});


