	const btnId = this.id.replace('btn', '');
	const url = btn.closest("tr").dataset.url;
	pressTime = 0;
	timerInterval = setInterval(() => {
		pressTime += 10;
		js.textContent = pressTime+"ms";
		}, 10);
		Timer = setTimeout(() => {
   copyToClipboard(daoru(url, btnId,true));
 }, 800);
});

btn.addEventListener('pointerup', function () {
clearTimeout(Timer);
clearInterval(timerInterval);
if (pressTime > 0 && pressTime < 800) {
const btnId = this.id.replace('btn', '');
const url = btn.closest("tr").dataset.url;
daoru(url, btnId);}
Timer2 = null;
Timer2 = setTimeout(() => {
ts.textContent = js.textContent = "";
 }, 900);
});

btn.addEventListener('pointercancel', function () {
clearTimeout(Timer);
clearInterval(timerInterval);
});

});

function copyToClipboard(text) {
navigator.clipboard.writeText(text).then(() => {
ts.textContent = "已复制订阅链接";
}).catch(err => {
 alert("复制失败: " + err);
});
}
</script>
${yhjs}
</body>
