sort="";x=0

//获取分类URL
function getUrls(){
try{
		urllist = []
		I = JSON.parse(java.ajax(api)).class.map($=>{
				turl = url+"&t="+$.type_id;
				urllist.push(turl);
				return $.type_name+"::"+turl;
			}).join('\n').split('\n');
		x = 0
		java.toast('获取分类成功,校验中')
		return verifyUrls()
	}
catch(e){//ajax失败会重新请求两次
		x++
