injectJs:
const video = document.getElementById('video');
        let isDragging = false;
        let startX = 0;
        let startTime = 0;

        video.addEventListener('touchstart', (e) => {
            isDragging = true;
            startX = e.touches[0].clientX;
            startTime = video.currentTime;
            video.pause();  // 暂停视频
        });

        video.addEventListener('touchmove', (e) => {
            if (!isDragging) return;
            const dx = e.touches[0].clientX - startX;
            const duration = video.duration;
            const change = (dx / video.clientWidth) * duration;
            video.currentTime = Math.min(Math.max(startTime + change, 0), duration);
        });

        video.addEventListener('touchend', () => {
            if (isDragging) {
                video.play();  // 继续播放视频
                isDragging = false;
            }
        });

ruleContent:
`<html>
    <head>
        <meta name="viewport" content="width=device-width">
        <style>
            body {
                margin: 0;
                font-family: Arial, sans-serif;
                background: linear-gradient(135deg, #ece9e6, #ffffff);
                display: flex;
                flex-direction: column;
                align-items: center;
                justify-content: flex-start;
                padding: 20px;
            }
            .container {
                width: 100%;
                max-width: 800px;
                background: #fff;
                border-radius: 10px;
                box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
                overflow: hidden;
                margin-bottom: 20px;
            }
            h3 {
                margin: 0;
                padding: 10px 20px;
                font-size: 0.9em;
                background: #333;
                color: #fff;
                border-radius: 10px 10px 0 0;
            }
            .video-container {
                width: 100%;
                background: #000;
                position: relative;
            }
            video {
                width: 100%;
                height: auto;
                display: block;
            }
            .info-container {
                padding: 20px;
            }
            .info-container p {
                margin: 10px 0;
                color: #555;
            }
            .info-container p span {
                font-weight: bold;
                color: #000;
            }
        </style>
    </head>
    <body>
        <div class="container">
            <h3>{{$.model.title}}</h3>
            <div class="video-container">
                <video id="video" src="{{$.model.url}}" poster="{{$.model.coverUrl}}" controls></video>
            </div>
        </div>
        <div class="info-container">
            <p>⌚️ 时长：<span>{{$.model.durationFormat}}</span></p>
            <p>📆 日期：<span>{{$.model.onlineTime##T|.000.*## }}</span></p>
            <p>🎥 播放：<span>{{$.model.viewCount}}次</span></p>
            <p>📩 收藏：<span>{{$.model.collectCount}}</span></p>
            <p>🕵 片商：<span>{{$.model.authors}}</span></p>
            <p>👤 上传：<span>{{$.model.uploader}}</span></p>
            <p>🔖 标签：<span>{{$.model.categories}},{{$.model.tags}}</span></p>
            <p>🏷 简介：<span>{{$.model.brief}}</span></p>
        </div>
        <script>
            // 获取视频元素
            const video = document.getElementById('video');
            let touchStartX = 0;
            let touchStartY = 0;
            let touchStartTime = 0;
            let isLongPress = false;

            // 滑动快进
            video.addEventListener('touchstart', (e) => {
                touchStartX = e.touches[0].clientX;
                touchStartY = e.touches[0].clientY;
                touchStartTime = Date.now();
                isLongPress = false;
            });

            video.addEventListener('touchmove', (e) => {
                const deltaX = e.touches[0].clientX - touchStartX;
                const deltaY = e.touches[0].clientY - touchStartY;

                // 如果是水平滑动，且未进入长按状态
                if (Math.abs(deltaX) > Math.abs(deltaY) && !isLongPress) {
                    const duration = video.duration;
                    const seekTime = video.currentTime + (deltaX / 50); // 每滑动100px快进1秒
                    video.currentTime = Math.min(Math.max(seekTime, 0), duration);
                }
                e.preventDefault(); // 防止默认行为（如页面滚动）
            });

            // 长按快进
            video.addEventListener('touchend', (e) => {
                const touchEndTime = Date.now();
                const touchDuration = touchEndTime - touchStartTime;

                // 如果长按时间超过500ms，进入长按快进状态
                if (touchDuration > 500) {
                    isLongPress = true;

                    // 在长按状态下，根据滑动距离快进
                    const deltaX = e.changedTouches[0].clientX - touchStartX;
                    const duration = video.duration;
                    const seekTime = video.currentTime + (deltaX / 100); // 每滑动100px快进1秒
                    video.currentTime = Math.min(Math.max(seekTime, 0), duration);
                } else {
                    isLongPress = false;
                }
            });
        </script>
    </body>
    </html>`

