async function loadComments(options = {}) {
  const {
    id = weiboId,
    isReply = false,
    isLoadMore = false,
    targetElement = isReply ? '#reply-list' : '#container',
    tElement = isReply ? '#reply-list .comments-list' : '.comments-list',
    containerTemplate = null,
    sort = isReply ? repSortType : sortType
  } = options;


  const loadingFlag = isReply ? repLoading : isLoading;
  if (loadingFlag) return;
  isReply ? repLoading = true : isLoading = true;


  let url;
  if (isReply) {
    url = \`https://weibo.com/ajax/statuses/buildComments?flow=\${sort}&is_reload=1&id=\${id}&is_show_bulletin=2&is_mix=1&fetch_level=1&max_id=\${isLoadMore ? repCurrentMaxId : 0}&count=20&locale=zh-CN\`
  } else {
   url =\`https://weibo.com/ajax/statuses/buildComments?flow=\${sort}&is_reload=1&id=\${id}&is_show_bulletin=2&is_mix=0&count=20&fetch_level=0&locale=zh-CN\`;


    //url = \`https://m.weibo.cn/comments/hotflow?id=\${id}&mid=\${id}&max_id_type=\${sort}\`;
    if (isLoadMore && currentMaxId) {
      url += \`&max_id=\${currentMaxId}\`;
    }
  }

  try {
    const response = await fetch(url, {
      headers: {
        'Accept': 'application/json',
        'X-Requested-With': 'XMLHttpRequest'
      },
      credentials: 'include'
    });

    const data = await response.json();


    if (data.ok === -100) {
      if (/error-message/.test(document.querySelector(targetElement).innerHTML)) {
        throw new Error("需要登录");
      }
      document.querySelector(targetElement).innerHTML += \`
        <div class="error-message">
          评论加载失败:请<a href="\${data.url}">访问首页或登录</a>后重试
        </div>
      \`;
      throw new Error("需登录");
    }


    const currentIdArray = isReply ? repMaxId : MaxId;
    const newMaxId = data.max_id;

    if (currentIdArray.includes(newMaxId)) {
      throw new Error("没有更多内容了");
    }

    if (isReply) {
      repCurrentMaxId = newMaxId;
      repMaxId.push(newMaxId);
    } else {
      currentMaxId = newMaxId;
      MaxId.push(newMaxId);
    }


    const commentsData = data.data;

    if (isReply && !isLoadMore && !document.querySelector('.reply-modal')) {
      initReplyModal(id);
    }

    if (isLoadMore) {
      document.querySelector(tElement).innerHTML += getcomments(commentsData, isReply);
    } else {
      if (data.ok === 0) throw new Error("没有内容");


      let content = \`<div class="comments-header">
            <div class="comments-title"><h3>评论（\${formatCount(data.total_number)}）</h3></div>
            <div class="sort-controls">
              <button id="sortHot" class="sort-btn \${sortType===0?'active':''}" 
                onclick="loadComments({sort:0})">热度</button>
              <button id="sortTime" class="sort-btn \${sortType===1?'active':''}" 
                onclick="loadComments({sort:1})">时间</button>
            </div>
          </div>\`;

      const html =  \`
        <div class="comments-section">
           \${isReply?"":content}        
          <div class="comments-list">\${getcomments(commentsData)}</div>
        </div>
      \`;
      document.querySelector(targetElement).innerHTML = html;
    }


  } catch (error) {
    if (isLoadMore) {
      showToast(/没有更多/.test(error.message)?error.message:"评论加载失败")
    } else if (!/没有内容|登录/.test(error.message)) {
      document.querySelector(targetElement).innerHTML = \`
        <div class="error-message">
          评论加载失败: \${error.message}
          <button onclick="loadComments(\${JSON.stringify(options)})">重试</button>
        </div>
      \`;
    }
  } finally {
    isReply ? repLoading = false : isLoading = false;
  }
}


