    <script>
    function formatTime(dateStr) {
    const date = new Date(dateStr);
    const year = date.getFullYear();
    const month = date.getMonth() + 1;     const day = date.getDate();
    const hours = date.getHours().toString().padStart(2, '0');
    const minutes = date.getMinutes().toString().padStart(2, '0');

    return \`\${year}年\${month}月\${day}日 \${hours}:\${minutes}\`;
}

function replaceurl(t){
let 	text = String(t).replace(/href=(\\/n\\/[^\\s]+)/g,'href=\"https://m.weibo.cn$1\"').replace(/\\/\\/s.weibo.com\\/weibo\\?q=/g,"https://m.weibo.cn/search?containerid=100103type%3D1%26q%3D").replace(/https?:\\/\\/weibo.com\\/\\d+\\/(\\d+)/g,'https://m.weibo.cn/a/$1');
	return text
	}

   function getcomments(list, isReply = false) {
  return list.map(item => {
    let details = "";
    if (item.pic_num !== 0) {
     let pic_ids =  item.url_struct[0].pic_ids;      
      const pic = item?.url_struct[0]?.pic_infos[pic_ids]?.large?.url.replace(/https?/,'https');
      details = \`
        <details class="comment-media">
          <summary><span class="media-preview">📷 查看图片</span></summary>
          <div class="comment_pic">
            <img src="\${pic}" alt="评论图片">
          </div>
        </details>\`;
    }

    let replies = "";
    if (item.comments && item.comments.length > 0) {
      replies = \`
        <div class="comment-replies">
          \${getcomments(item.comments, true)}
          \${item.total_number > 1 ? \`
            <div class="morerep" onclick="loadComments({id:'\${item.id}',isReply:true})">
              共\${item.total_number}条回复
            </div>\` : ''}
        </div>\`;
    }

    return \`
      <div class="comment-item">
        <div class="comment-user">
          <img class="user-avatar" src="\${item.user.profile_image_url}" onclick="window.open('https://m.weibo.cn/n/\${item.user.screen_name}')">
          <div class="user-info">
            <span class="user-name" onclick="window.open('https://m.weibo.cn/n/\${item.user.screen_name}')">\${item.user.screen_name}</span>
            <span class="comment-time">\${formatTime(item.created_at)}</span>
          </div>
          <div class="like_counts"><span>♡\${formatCount(item.like_counts)}</span></div>
        </div>
        <div class="comment-text">\${replaceurl(item.text).replace(/(<img.*?>)/g,'<span class="url-icon">$1</span>')}</div>
        \${details}
        \${replies}
      </div>\`;
  }).join('');
}
