}
function getInput1(){
text=$("#input").value;
list=text.match(/<a.*?href="([^"]+)".*?>([\s\S]+?)<\/a>/g);
html=""
if(list.length==0){
html="格式化失败！！！请检查是否为带html标签"
}else{
html=""
}
