<!DOCTYPE html>
<html lang="zh-CN">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>代码预览工具</title>
  <style>
    body {
      font-family: Arial, sans-serif;
      margin: 0;
      padding: 10px;
      background-color: #f9f9f9;
    }
    h1 {
      font-size: 20px;
      margin: 0 0 10px 0;
      color: #333;
    }
    h2 {
      font-size: 16px;
      margin: 0 0 8px 0;
      color: #555;
    }
    .container {
      display: flex;
      flex-direction: column;
      gap: 10px;
    }
    .code-input, .preview {
      width: 100%;
    }
    .code-wrapper {
      display: flex;
      gap: 10px;
    }
    .line-numbers {
      width: 30px;
      padding: 8px 0;
      font-family: monospace;
      font-size: 14px;
      text-align: right;
      color: #888;
      background-color: #f0f0f0;
      border: 1px solid #ccc;
      border-radius: 4px;
      box-sizing: border-box;
      user-select: none;
      overflow-y: hidden;
      height: 150px;
    }
    textarea {
      width: 100%;
      height: 150px;
      padding: 8px;
      font-family: monospace;
      font-size: 14px;
      border: 1px solid #ccc;
      border-radius: 4px;
      box-sizing: border-box;
      line-height: 1.5;
      resize: none;
      overflow-y: auto;
    }
    .preview {
      border: 1px solid #ccc;
      padding: 10px;
      border-radius: 4px;
      background-color: #fff;
    }
    .console {
      margin-top: 10px;
      padding: 8px;
      background-color: #000;
      color: #fff;
      font-family: monospace;
      font-size: 12px;
      border-radius: 4px;
      height: 80px;
      overflow-y: auto;
      box-sizing: border-box;
    }
    button {
      margin-top: 8px;
      padding: 8px 16px;
      font-size: 14px;
      background-color: #007bff;
      color: white;
      border: none;
      border-radius: 4px;
      cursor: pointer;
    }
    button:hover {
      background-color: #0056b3;
    }
    .preview-output {
      height: 400px;
      border: 1px solid #ccc;
      border-radius: 4px;
      background-color: #fff;
      overflow: auto;
      box-sizing: border-box;
      padding: 10px;
    }
    .button-group {
      display: flex;
      gap: 10px;
      align-items: center;
    }
    .tab-group {
      display: flex;
      gap: 10px;
      margin-bottom: 10px;
    }
    .tab-button {
      padding: 8px 16px;
      font-size: 14px;
      background-color: #f0f0f0;
      color: #333;
      border: 1px solid #ccc;
      border-radius: 4px;
      cursor: pointer;
    }
    .tab-button.active {
      background-color: #007bff;
      color: white;
      border-color: #007bff;
    }
    .code-input {
      display: none;
    }
    .code-input.active {
      display: block;
    }
    .preview-content {
      display: none;
    }
    .preview-content.active {
      display: block;
    }
    .mode-switch {
      margin-top: 10px;
    }
  </style>
</head>
<body>
  <div class="container">
      <div class="preview">
      <h2>控制台输出</h2>
      <div id="consoleOutput" class="console"></div>
    </div>
    <div class="code-input active">

      <div class="tab-group">
        <button class="tab-button active" onclick="switchTab('previewTab')">预览代码</button>
        <button class="tab-button" onclick="switchTab('debugTab')">调试 JS</button>
        <button class="tab-button" onclick="switchTab('injectTab')">注入 JS</button>
        <button onclick="clearConsole()">清除控制台</button>
      </div>

      <div class="code-wrapper">
        <div id="lineNumbers" class="line-numbers">1</div>
        <textarea id="codeInput" placeholder="请输入 HTML、CSS 或 JavaScript 代码..." oninput="updateLineNumbers()" onscroll="syncLineNumbers()"></textarea>
      </div>

      <div class="button-group">
        <button id="previewButton" onclick="previewCode()">确认</button>
        <button id="debugButton" onclick="debugJS()" style="display: none;">调试</button>
        <button id="injectButton" onclick="injectJS()" style="display: none;">注入</button>
        <button onclick="switchMode('code')">代码</button>
        <button onclick="switchMode('url')">网址</button>
        <button onclick="switchPreview('preview')">预览</button>
        <button onclick="switchPreview('source')">源码</button>
         <button onclick="clearCode()">清除</button>
      </div>

    </div>


        <div class="preview">
      <div id="previewOutput" class="preview-content active preview-output"></div>
      <div id="sourceCodeOutput" class="preview-content preview-output"></div>
    </div>
  </div>

  <script>
    // 数据存储
    var codeData = {
      preview: '',
      debug: '',
      inject: ''
    };

    // 当前选中的标签
    var currentTab = 'previewTab';

    // 当前模式（代码模式或网址模式）
    var currentMode = 'code';

    // 切换标签
    function switchTab(tab) {
      currentTab = tab;

      // 更新按钮状态
      document.querySelectorAll('.tab-button').forEach(button => button.classList.remove('active'));
      document.querySelector(`.tab-button[onclick="switchTab('${tab}')"]`).classList.add('active');

      // 显示对应的输入框和按钮
      var codeInput = document.getElementById('codeInput');
      var previewButton = document.getElementById('previewButton');
      var debugButton = document.getElementById('debugButton');
      var injectButton = document.getElementById('injectButton');

      if (tab === 'previewTab') {
        codeInput.value = codeData.preview;
        previewButton.style.display = 'inline-block';
        debugButton.style.display = 'none';
        injectButton.style.display = 'none';
      } else if (tab === 'debugTab') {
        codeInput.value = codeData.debug;
        previewButton.style.display = 'none';
        debugButton.style.display = 'inline-block';
        injectButton.style.display = 'none';
      } else if (tab === 'injectTab') {
        codeInput.value = codeData.inject;
        previewButton.style.display = 'none';
        debugButton.style.display = 'none';
        injectButton.style.display = 'inline-block';
      }

      // 更新行号
      updateLineNumbers();
    }
function clearCode(){
if (currentTab === 'previewTab') {
        codeData.preview = ""
        codeInput.value = "";
      } else if (currentTab === 'debugTab') {
      codeData.debug = "";
        codeInput.value = ""

      } else if (currentTab === 'injectTab') {
      codeData.inject ="";
        codeInput.value = "";
      }

}
    // 更新行号
    function updateLineNumbers() {
      var codeInput = document.getElementById('codeInput');
      var lineNumbers = document.getElementById('lineNumbers');
      var lines = codeInput.value.split('\n').length;
      lineNumbers.innerHTML = Array.from({ length: lines }, (_, i) => i + 1).join('<br>');

      // 保存当前输入框的数据
      if (currentTab === 'previewTab') {
        codeData.preview = codeInput.value;
      } else if (currentTab === 'debugTab') {
        codeData.debug = codeInput.value;
      } else if (currentTab === 'injectTab') {
        codeData.inject = codeInput.value;
      }
    }

    // 同步行号滚动
    function syncLineNumbers() {
      var codeInput = document.getElementById('codeInput');
      var lineNumbers = document.getElementById('lineNumbers');
      lineNumbers.scrollTop = codeInput.scrollTop;
    }

    // 捕获本页面的 console 输出
    var consoleOutput = document.getElementById('consoleOutput');
    var originalConsole = {
      log: console.log,
      error: console.error,
      warn: console.warn,
      info: console.info,
    };

    function captureConsole(type) {
      return function () {
        // 将输出保存到控制台输出区域
        var message = Array.from(arguments).join(' ');
        var logElement = document.createElement('div');
        logElement.textContent = `[${type}] ${message}`;
        consoleOutput.appendChild(logElement);

        // 调用原始的 console 方法
        originalConsole[type].apply(console, arguments);
      };
    }

    // 重写本页面的 console 方法
    console.log = captureConsole('log');
    console.error = captureConsole('error');
    console.warn = captureConsole('warn');
    console.info = captureConsole('info');

    // 预览代码
    function previewCode() {
      if (currentMode === 'code') {
        // 获取用户输入的代码
        var code = codeData.preview;

        // 清空预览区域
        var previewOutput = document.getElementById('previewOutput');
        previewOutput.innerHTML = '';

        // 移除所有 <a> 标签的 target="_blank" 属性
        code = code.replace(/<a\s+([^>]*)\s*target="_blank"\s*([^>]*)>/gi, '<a $1 $2>');

        // 创建一个 iframe 来隔离代码环境
        var iframe = document.createElement('iframe');
        iframe.style.width = '100%';
        iframe.style.height = '100%';
        iframe.style.border = 'none';
        previewOutput.appendChild(iframe);

        // 捕获 iframe 的 console 输出
        var iframeWindow = iframe.contentWindow;
        iframeWindow.console.log = captureConsole('log');
        iframeWindow.console.error = captureConsole('error');
        iframeWindow.console.warn = captureConsole('warn');
        iframeWindow.console.info = captureConsole('info');

        // 将处理后的代码写入 iframe
        var iframeDoc = iframe.contentDocument || iframe.contentWindow.document;
        iframeDoc.open();
        iframeDoc.write(`
          <!DOCTYPE html>
          <html>
            <head>
              <style>
                ${code.includes('<style>') ? '' : 'body { margin: 0; padding: 0; }'}
              </style>
            </head>
            <body>
              ${code}
              <script>
                ${code.includes('<script>') ? '' : ''}
              <\/script>
            </body>
          </html>
        `);
        iframeDoc.close();

        // 显示源码
        updateSourceCode(iframeDoc.documentElement.outerHTML);
      } else if (currentMode === 'url') {
        // 加载网址
        var url = codeData.preview.trim();
        if (!url) {
          console.error('请输入网址。');
          return;
        }

        // 清空预览区域
        var previewOutput = document.getElementById('previewOutput');
        previewOutput.innerHTML = '';

        // 创建一个 iframe 来加载网址
        var iframe = document.createElement('iframe');
        iframe.style.width = '100%';
        iframe.style.height = '100%';
        iframe.style.border = 'none';
        previewOutput.appendChild(iframe);

        // 捕获 iframe 的 console 输出
        var iframeWindow = iframe.contentWindow;
        iframeWindow.console.log = captureConsole('log');
        iframeWindow.console.error = captureConsole('error');
        iframeWindow.console.warn = captureConsole('warn');
        iframeWindow.console.info = captureConsole('info');

        // 加载网址
        iframe.src = url;

        // 显示源码
        iframe.onload = function () {
          var iframeDoc = iframe.contentDocument || iframe.contentWindow.document;
          updateSourceCode(iframeDoc.documentElement.outerHTML);
        };
      }
    }

    // 更新源码预览
    function updateSourceCode(source) {
      var sourceCodeOutput = document.getElementById('sourceCodeOutput');
      sourceCodeOutput.textContent = source;
    }

    // 调试 JS 函数
    function debugJS() {
      var code = codeData.debug;

      try {
        // 使用 eval 执行 JavaScript 代码
        eval(code);
      } catch (error) {
        // 捕获错误并解析行号
        var errorLine = error.stack.match(/<anonymous>:(\d+):\d+/);
        if (errorLine) {
          console.error(`调试失败，第 ${errorLine[1]} 行：${error.message}`);
        } else {
          console.error('调试失败:', error);
        }
      }
    }

    // 注入 JS 到 iframe
    function injectJS() {
      var code = codeData.inject;
      var iframe = document.querySelector('#previewOutput iframe');

      if (!iframe) {
        console.error('请先点击“确认预览”创建 iframe。');
        return;
      }

      try {
        // 获取 iframe 的 window 对象
        var iframeWindow = iframe.contentWindow;

        // 在 iframe 中执行 JavaScript 代码
        iframeWindow.eval(code);

        // 更新源码预览
        var iframeDoc = iframe.contentDocument || iframe.contentWindow.document;
        updateSourceCode(iframeDoc.documentElement.outerHTML);
      } catch (error) {
        console.error('注入 JS 失败:', error);
      }
    }

    // 切换预览效果和源码预览
    function switchPreview(type) {
      var previewOutput = document.getElementById('previewOutput');
      var sourceCodeOutput = document.getElementById('sourceCodeOutput');

      if (type === 'preview') {
        previewOutput.classList.add('active');
        sourceCodeOutput.classList.remove('active');
      } else if (type === 'source') {
        previewOutput.classList.remove('active');
        sourceCodeOutput.classList.add('active');
      }
    }

    // 切换模式（代码模式或网址模式）
    function switchMode(mode) {
      currentMode = mode;
      var codeInput = document.getElementById('codeInput');

      if (mode === 'code') {
        codeInput.placeholder = '请输入 HTML、CSS 或 JavaScript 代码...';
      } else if (mode === 'url') {
        codeInput.placeholder = '输入网址，例如：https://example.com';
      }
    }

    // 清除控制台
    function clearConsole() {
      var consoleOutput = document.getElementById('consoleOutput');
      consoleOutput.innerHTML = '';
    }

    // 默认显示预览标签
    switchTab('previewTab');
  </script>
</body>
</html>

