@js:
var result = [];
java.toast("🔥正在刷新列表,请稍等！");
var push = (title, url, size) => result.push({
    "title": title,
    "url": url,
    "style": {
        "layout_flexGrow": 1,
        "layout_flexBasisPercent": size
    }
});
var baseUrl = "https://www.wxsck.com/";
var tag = java.ajax(baseUrl);
if (tag) {
    var a = org.jsoup.Jsoup.parse(tag).select(".breadcrumb li a");
    push("🔥分类小说🔥", null, 1);
    for (var i = 0; i < a.length -1; i++) {
        var title = a[i].text();
        var url = a[i].attr("href");
        var size = 0.25;
        title = String(title).replace(/\s/g, "");
        url = String(url).replace(/\.html/,'_{{page}}.html');      
        push(title, url, size);
    }
} else {
  java.toast("🤔列表刷新失败！！！");     
} 
JSON.stringify(result);

