{{
	var su=source.getKey()
var htmlUrl=java.get(su,{}).headers('Location')
java.log(`重定向：${htmlUrl[0]}`)
var posturl=htmlUrl[0]?htmlUrl[0]:(su.includes("#")?su.match(/([^\#]+)\#/)[1]:su)
posturl = posturl.slice(-1)=='/'?posturl.slice(0,-1):posturl
java.log(`请求：${posturl}`)
cookie.removeCookie(posturl);
ck=cookie.getCookie(posturl);

cookie.setCookie(posturl,String(ck).replace(/ss_search_delay=.*/,""));
posturl
}}/search/,{
  "body": "searchkey={{key}}",
