java.toast("🔥正在刷新列表,请稍等！");
var push = (title, url, size) => result.push({
  "title": title,
  "url": url,
  "style": {
    "layout_flexGrow": 1,
    "layout_flexBasisPercent": size
  }
});

var fl = java.ajax("https://yybook.vip/all.html");
var ph = java.ajax("https://yybook.vip/all/id/4.html");
var wb = java.ajax("https://yybook.vip/all/id/8.html");

if (fl && ph && wb) {

  a = org.jsoup.Jsoup.parse(fl).select("#book-nav a");
  b = org.jsoup.Jsoup.parse(ph).select("#book-nav a");
  c = org.jsoup.Jsoup.parse(wb).select("#book-nav a");

  push("🔥全部小说🔥", null, 1);
  for (var i = 0; i < a.length; i++) {
    title = a[i].text();
    url = a[i].attr("href");
    size = 0.33;
    title = String(title).replace(/\s/g, "");
    url = String(url).replace(/\/d+\.html$/, "")+"?page={{page}}";
    push(title, url, size);
  };

  push("🔥男生小说🔥", null, 1);
  for (var i = 0; i < b.length; i++) {
    title = b[i].text();
    url = b[i].attr("href");
    size = 0.33;
    title = String(title).replace(/\s/g, "");
    url = String(url).replace(/\/d+\.html$/, "")+"?page={{page}}";
    push(title, url, size);
  };

  push("🔥女生小说🔥", null, 1);
  for (var i = 0; i < c.length; i++) {
    title = c[i].text();
    url = c[i].attr("href");
    size = 0.33;
    title = String(title).replace(/\s/g, "");
    url = String(url).replace(/\/d+\.html$/, "")+"?page={{page}}";
    push(title, url, size);
  };

} else {
  java.toast("🤔列表刷新失败！！！");
}

JSON.stringify(result);

