exploreUrl:
@js:
function getCategories() {
    // 获取标签分类
    let tagHtml = java.ajax("https://www.yeduge.com/tags/");
    let tagDoc = org.jsoup.Jsoup.parse(tagHtml);
    let tagList = tagDoc.select(".tag-list li a");

    let tags = Array.from(tagList, (a) => ({
        "title": a.ownText().trim(),
        "url": a.attr("href") + "{{page}}.html",
        "style": {
            "layout_flexGrow": 1,
            "layout_flexBasisPercent": 0.25
        }
    }));

    // 获取分类目录
    let catHtml = java.ajax("https://www.yeduge.com/categories/");
    let catDoc = org.jsoup.Jsoup.parse(catHtml);
    let catList = catDoc.select(".category-list li a");

    let categories = Array.from(catList, (a) => ({
        "title": a.ownText().trim(),
        "url": a.attr("href") + "?page={{page}}",
        "style": {
            "layout_flexGrow": 1,
            "layout_flexBasisPercent": 0.25
        }
    }));

    // 添加完本小说和热门小说
    let additionalCategories = [
        {
            "title": "完本小说",
            "url": "https://www.yeduge.com/rank/complete/{{page}}.html",
            "style": {
                "layout_flexGrow": 1,
                "layout_flexBasisPercent": 0.25
            }
        },
        {
            "title": "热门小说",
            "url": "https://www.yeduge.com/rank/hot/{{page}}.html",
            "style": {
                "layout_flexGrow": 1,
                "layout_flexBasisPercent": 0.25
            }
        }
    ];

    // 合并所有数组
    let allCategories = tags.concat(categories, additionalCategories);

    return JSON.stringify(allCategories);
}

// 执行并返回结果
getCategories();

loginUrl:
function login() {
    try {
        var url = baseUrl.replace(/\/+$/, '').replace(/[^\w.:/-]/g, '');
        var ur = source.getLoginInfoMap();
        var params = `username=${ur.get("账号")}&password=${ur.get("密码")}`;
        var r = java.post(`${url}/api/login.php`, params, {"method": "POST"});

        // 解析响应体
        var resp = JSON.parse(r.body());

        // 处理登录结果
        if (resp.success) {
            const cookies = Array.from(r.cookies(), 
    ([k, v]) => `${k}=${v}`).join(';');
            return source.putLoginHeader(JSON.stringify({Cookie: cookies}));
        } else {
            throw new Error(`登录失败: ${resp.message || '未知错误'}`);
        }
    } catch (e) {
        java.log(`登录异常: ${e.message}\n请求参数: ${params}`);
        throw e;
    }
}

