function copy(txt) {
	let htm = `data:text/html;charset=utf-8,<html>
<head>
<meta name="viewport" content="width=device-width, initial-scale=1.0">
<style>
body {
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	height: 60vh;}
</style>
</head>
<body>
<textarea id="copyt" rows="3">${txt}</textarea>
<br>
<button id="btn1" onclick="copy()">复制</button>
<script>
document.addEventListener("DOMContentLoaded", function () {
const textarea = document.getElementById("copyt");
textarea.style.height = "auto";
textarea.style.height = textarea.scrollHeight + "px";});
function copy() {
const cpt = document.getElementById("copyt");
cpt.select();
document.execCommand("copy");}
</script>
</body></html>`;
this.java.startBrowser(htm,'复制');
	}


