let tkurl = 'https://oauth.secure.pixiv.net/auth/token';
let headers = gethd();
let body = `code_verifier=${cv}&code=${code[1]}&grant_type=authorization_code&redirect_uri=https://app-api.pixiv.net/web/v1/users/auth/pixiv/callback&client_id=MOBrBDS8blbauoSck0ZfDbtuzpyT&client_secret=lsACyCD94FhDUtGTXi3QzcFE2uU1hqtDaKeqrdwj&include_policy=true`;
let tkr = java.post(tkurl,body,headers);
