keyUrl=java.toURL(source.key).origin.replace(`:${source.key}`,"")
try{
	html=org.jsoup.Jsoup.parse(java.ajax(keyUrl))
	form=html.select("form[refs]")
	action=form.attr("action")
	token=form.select('[name~=token]').attr("value")

	sorts=html.select("ul.dropdown-menu a")
	orders=["desc","asc"]
	push("⬇️降序","",1,0.4)
	push("⬆️升序","",1,0.4)
	sorts.forEach(sort=>{
		title=sort.text()
		orders.forEach(order=>{
			uri=`${action},{"body": "_return=${java.encodeURI(keyUrl)}&_method=PATCH&_token=${token}&sort=${sort.attr("data-sort-value")}&order=${order}","method": "POST"}`
			push(title,$$$(uri),1,0.4)
			})
		})
		java.toast("\n✅发现获取成功")
	}catch(e){
		java.log(e)
		java.toast("\n🚫发现获取失败……")
