@js:JSON.parse(result).map(o=> {
    var J = org.jsoup.Jsoup.parse(java.ajax(baseUrl.match(/(.+?\/\/.+?\/)/)[1] + 'source/' + o.sourceUuid));
    return {
        name: o.title,
        author: o.author,
        tags: o.keyName,
        num:o.showTotalCount+'Â≠ó',
        latest: String(J.select('.m-book-recent a').text()).replace(/[\(Ôºà„Äê].*[Ê±ÇÊõ¥Ë∞¢‰πêÂèëÂêà].*[„ÄëÔºâ\)]/,'') +String(J.select('.m-book-recent p').text()).replace(/\s*(\d+-\d+-\d+).+/, 'Ôºà$1Ôºâ').replace(/\s*(\d+)Êúà(\d+)Êó•.+/, 'Ôºà$1-$2Ôºâ'),
        intro: o.description.replace(/<\/?\w+>/g, '').trim(),
        cover: o.coverImage,
        catalogUrl: '/reader/book/info.json?catalog_only=true&source_uuid=' + o.sourceUuid
    }
});

